function geoFindMe(){const o=document.getElementById("out");navigator.geolocation?(o.innerHTML="<p>Locating…</p>",navigator.geolocation.getCurrentPosition(function(e){let n=e.coords.latitude,t=e.coords.longitude;o.innerHTML="<p>Latitude is "+n+"° <br>Longitude is "+t+"°</p>";let i=new Image;i.src="https://maps.googleapis.com/maps/api/staticmap?center="+n+","+t+"&zoom=13&size=300x300&sensor=false",o.appendChild(i)},function(){o.innerHTML="Unable to retrieve your location"})):o.innerHTML="<p>Geolocation is not supported by your browser</p>"}